INSERT INTO `SEARCH` VALUES ('harry potter','{\"kind\": \"books#volumes\", \"items\": [{\"id\": \"lMM4jgEACAAJ\", \"etag\": \"5jpRlkdheEc\", \"kind\": \"books#volume\", \"saleInfo\": {\"country\": \"US\", \"isEbook\": false, \"saleability\": \"NOT_FOR_SALE\"}, \"selfLink\": \"https://www.googleapis.com/books/v1/volumes/lMM4jgEACAAJ\", \"accessInfo\": {\"pdf\": {\"isAvailable\": false}, \"epub\": {\"isAvailable\": false}, \"country\": \"US\", \"embeddable\": false, \"viewability\": \"NO_PAGES\", \"publicDomain\": false, \"webReaderLink\": \"http://play.google.com/books/reader?id=lMM4jgEACAAJ&hl=&printsec=frontcover&source=gbs_api\", \"accessViewStatus\": \"NONE\", \"quoteSharingAllowed\": false, \"textToSpeechPermission\": \"ALLOWED\"}, \"searchInfo\": {\"textSnippet\": \"Filled with intricate illustrations and elaborate designs used in the making of the Harry Potter films, this book invites you to imbue the wizarding world with color in your own explorations of Hogwarts Castle, the Forbidden Forest, and ...\"}, \"volumeInfo\": {\"title\": \"Harry Potter Coloring Book\", \"authors\": [\"Inc. Scholastic\"], \"infoLink\": \"http://books.google.com/books?id=lMM4jgEACAAJ&dq=harry+potter&hl=&source=gbs_api\", \"language\": \"en\", \"pageCount\": 96, \"printType\": \"BOOK\", \"publisher\": \"Scholastic Incorporated\", \"categories\": [\"Juvenile Fiction\"], \"imageLinks\": {\"thumbnail\": \"http://books.google.com/books/content?id=lMM4jgEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api\", \"smallThumbnail\": \"http://books.google.com/books/content?id=lMM4jgEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api\"}, \"description\": \"Packed with stunning pieces of artwork from the Warner Bros. archive, this deluxe coloring book gives fans the chance to color in the vivid settings and beloved characters of J.K. Rowling\'s wizarding world. Containing intricate line drawings used in the making of the Harry Potter films, this coloring book includes fan-favorite scenes, creatures, and characters: from Dobby and baby Norbert to Quidditch games and the unforgettable final battle between Harry and Lord Voldemort. Unique and interactive, Harry Potter: The Official Coloring Book is a perfect collector\'s item for all fans of the blockbuster saga--whether it\'s for those who grew up with Harry, Ron, and Hermione, or those who are discovering the magic for the very first time.\", \"previewLink\": \"http://books.google.com/books?id=lMM4jgEACAAJ&dq=harry+potter&hl=&cd=1&source=gbs_api\", \"ratingsCount\": 2, \"readingModes\": {\"text\": false, \"image\": false}, \"averageRating\": 3, \"publishedDate\": \"2015-11-10\", \"contentVersion\": \"preview-1.0.0\", \"maturityRating\": \"NOT_MATURE\", \"allowAnonLogging\": false, \"canonicalVolumeLink\": \"https://books.google.com/books/about/Harry_Potter_Coloring_Book.html?hl=&id=lMM4jgEACAAJ\", \"industryIdentifiers\": [{\"type\": \"ISBN_10\", \"identifier\": \"1338029991\"}, {\"type\": \"ISBN_13\", \"identifier\": \"9781338029994\"}]}}, {\"id\": \"kLAoswEACAAJ\", \"etag\": \"+nBVfQn29lg\", \"kind\": \"books#volume\", \"saleInfo\": {\"country\": \"US\", \"isEbook\": false, \"saleability\": \"NOT_FOR_SALE\"}, \"selfLink\": \"https://www.googleapis.com/books/v1/volumes/kLAoswEACAAJ\", \"accessInfo\": {\"pdf\": {\"isAvailable\": false}, \"epub\": {\"isAvailable\": false}, \"country\": \"US\", \"embeddable\": false, \"viewability\": \"NO_PAGES\", \"publicDomain\": false, \"webReaderLink\": \"http://play.google.com/books/reader?id=kLAoswEACAAJ&hl=&printsec=frontcover&source=gbs_api\", \"accessViewStatus\": \"NONE\", \"quoteSharingAllowed\": false, \"textToSpeechPermission\": \"ALLOWED\"}, \"searchInfo\": {\"textSnippet\": \"As an overworked employee of the Ministry of Magic, a husband, and a father, Harry Potter struggles with a past that refuses to stay where it belongs while his youngest son, Albus, finds the weight of the family legacy difficult to bear.\"}, \"volumeInfo\": {\"title\": \"Harry Potter and the Cursed Child\", \"authors\": [\"J. K. Rowling\", \"Jack Thorne\", \"John Tiffany\"], \"infoLink\": \"http://books.google.com/books?id=kLAoswEACAAJ&dq=harry+potter&hl=&source=gbs_api\", \"language\": \"en\", \"pageCount\": 352, \"printType\": \"BOOK\", \"publisher\": \"Sphere\", \"categories\": [\"Fiction\"], \"imageLinks\": {\"thumbnail\": \"http://books.google.com/books/content?id=kLAoswEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api\", \"smallThumbnail\": \"http://books.google.com/books/content?id=kLAoswEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api\"}, \"description\": \"The official playscript of the original West End production of Harry Potter and the Cursed Child. It was always difficult being Harry Potter and it isn\'t much easier now that he is an overworked employee of the Ministry of Magic, a husband, and father of three school-age children. While Harry grapples with a past that refuses to stay where it belongs, his youngest son Albus must struggle with the weight of a family legacy he never wanted. As past and present fuse ominously, both father and son learn the uncomfortable truth: sometimes, darkness comes from unexpected places. The playscript for Harry Potter and the Cursed Child was originally released as a \'special rehearsal edition\' alongside the opening of Jack Thorne\'s play in London\'s West End in summer 2016. Based on an original story by J.K. Rowling, John Tiffany and Jack Thorne, the play opened to rapturous reviews from theatregoers and critics alike, while the official playscript became an immediate global bestseller. This revised paperback edition updates the \'special rehearsal edition\' with the conclusive and final dialogue from the play, which has subtly changed since its rehearsals, as well as a conversation piece between director John Tiffany and writer Jack Thorne, who share stories and insights about reading playscripts. This edition also includes useful background information including the Potter family tr